mapper.select:
select t2.*
    from (
      select t1.*,
          rownum as rn
        from (
            select
                t.TASK_ID as taskId,
                t.TASK_NAME as taskName,
                t.EXPRESSION as expression,
                t.PROGRESS as progress,
                t.STATE as state,
                t.TASK_CASE_ID as taskCaseId,
                t.DJSJ as djsj
            from TB_ST_REGIONALTASK t
              where t.TASK_CASE_ID = #{taskCaseId}
              order by t.djsj
      ) t1
      where rownum  <=  #{endNum}
    ) t2
  where rn  >  #{startNum}
