mapper.select:
SELECT XXZJBH
FROM TB_ST_LOGINLOG
WHERE XXZJBH = #{xxzjbh}

SELECT * FROM ( SELECT t1.*, rownum as rn FROM ( SELECT t.ASJBH as asjbh, t.CXR_SFZH as cxrSfzh, t.CXR_XM as cxrXm, t.CXR_JH as cxrJh, t.CXR_LXDH as cxrLxdh, t.CXR_DLSJ as cxrDlsj FROM TB_ST_LOGINLOG t
and t.CXR_SFZH like CONCAT(CONCAT('%',#{cxrSfzh}),'%')
and t.CXR_XM like CONCAT(CONCAT('%',#{cxrXm}),'%')
and t.CXR_JH like CONCAT(CONCAT('%',#{cxrJh}),'%')
and t.CXR_LXDH like CONCAT(CONCAT('%',#{cxrLxdh}),'%')
and t.ASJBH like CONCAT(CONCAT('%',#{asjbh}),'%')
and t.CXR_DLSJ >= to_date(#{cxrKssj},'yyyy-MM-dd')
and t.CXR_DLSJ < to_date(#{cxrJssj},'yyyy-MM-dd')
)t1
and rownum <= #{end }
) t2
rn > #{begin }
